import React from 'react'
import styled, { injectGlobal } from 'styled-components'

injectGlobal`
  @font-face {
    font-family: 'fontello';
    src: url('data:application/octet-stream;base64,') format('truetype');
  }
`

const Icon = styled.span`
  &:before, &:before {
   font-family: "fontello";
   font-style: normal;
   font-weight: normal;
   speak: none;

   display: inline-block;
   text-decoration: inherit;
   width: 1em;
   margin-right: .2em;
   text-align: center;

   /* For safety - reset parent styles, that can break glyph codes*/
   font-variant: normal;
   text-transform: none;

   /* fix buttons height, for twitter bootstrap */
   line-height: 1em;

   /* Animation center compensation - margins should be symmetric */
   /* remove if not needed */
   margin-left: .2em;

   /* Uncomment for 3D effect */
   /* text-shadow: 1px 1px 1px rgba(127, 127, 127, 0.3); */
  }
`

export const IconOk = styled(Icon)`
  &:before { content: '\\e800'; }
`
export const IconCancel = styled(Icon)`
  &:before { content: '\\e801'; }
`
export const IconPin = styled(Icon)`
  &:before {
    content: '\\e802';
    transform: rotate(90deg);
  }
`
export const IconUnpin = styled(Icon)`
  &:before {
    content: '\\e802';
    transform: rotate(45deg);
  }
`
export const IconError = styled(Icon)`
  &:before { content: '\\e803'; }
`
export const IconFold = styled(Icon)`
  &:before {
    content: '\\f147';
    font-size: 80%;
  }
`
export const IconUnfold = styled(Icon)`
  &:before {
    content: '\\f196';
    font-size: 80%;
  }
`
export const IconPending = styled(Icon)`
  &:before { content: '\\f252'; }
`
